mapper.sql:
WHERE 1=1 AND board_menu_code = #{board_menu_code} AND board_useyn != 'N' AND board_show = 'Y'
AND ( board_idx = #{board_idx} OR board_idx = (select max(board_idx) from t_board where board_idx < #{board_idx} and board_menu_code = #{board_menu_code} and board_useyn != 'N') OR board_idx = (select min(board_idx) from t_board where board_idx > #{board_idx} and board_menu_code = #{board_menu_code} and board_useyn != 'N') )
AND board_cate_L = #{board_cate_L}
AND board_cate_L in
#{code}
AND board_reg_date = #{board_reg_date}
AND board_title like CONCAT('%',#{board_search},'%')
AND board_reg_pass = SHA2( #{board_reg_pass}, 256 )
AND board_idx = #{board_idx}

